const fs = require('fs');
 
function genererLiensEtSauvegarder(nbLiens) {
  const caracteres = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz123456789-';
  const longueurLien = 182; // Longueur totale du lien
  const longueurCode = longueurLien - 60 - 41; // Longueur de la section de code aléatoire
  let listeLiens = [];
 
  for (let i = 0; i < nbLiens; i++) {
    let codeRandom = Array.from({ length: longueurCode }, () => caracteres.charAt(Math.floor(Math.random() * caracteres.length))).join('');
    let lien = `discord.com/billing/partner-promotions/1161769335442899024/${codeRandom}`;
    listeLiens.push(lien);
  }
 
  const data = listeLiens.join('\n');
 
  fs.writeFile('liens.txt', data, (err) => {
    if (err) throw err;
    console.log('Les liens ont été sauvegardés dans liens.txt');
  });
 
  console.log(`Nombre de liens générés : ${listeLiens.length}`);
}
 
// Générer et sauvegarder 20000 liens (vous pouvez changer le nombre selon vos besoins)
genererLiensEtSauvegarder(2000000);